    def jisuan(self):
        global Q,Rm,T,P1,P2,V1,V2,ρ1,ρ2
        self.qiti()
        T0 = 273.15
        T = self.lineEdit_3.text()
        P1=self.lineEdit_4.text()
        P2=self.lineEdit_5.text()
        V1 = self.lineEdit_6.text()
        V2 = self.lineEdit_7.text()
